public class Arrays_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				
		
		String [] paises=new String[8];
		// llenar matriz
		for(int i=0;i<8;i++) {
			paises[i]=JOptionPane.showInputDialog("Introduce país" + (i+1));
		}
		// mostrarla
		for(String i:paises) {
		System.out.println("pais: "+i);	
			
		}
		
		
		
	}

}
